mapper.select:
SELECT ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NICKNAME, MEMBER_KAKAO_PROFILE_URL, MEMBER_IMG_NAME, MEMBER_IMG_PATH, MEMBER_PAYMENT_TOTAL, MEMBER_CREATE_TIME, MEMBER_UPDATE_TIME, MEMBER_INTRODUCTION, MEMBER_STATUS
FROM TBL_MEMBER
WHERE ID = #{id}

SELECT ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NICKNAME, MEMBER_KAKAO_PROFILE_URL, MEMBER_IMG_NAME, MEMBER_IMG_PATH, MEMBER_PAYMENT_TOTAL, MEMBER_CREATE_TIME, MEMBER_UPDATE_TIME, MEMBER_INTRODUCTION, MEMBER_STATUS
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_STATUS = 'ACTIVE', MEMBER_UPDATE_TIME = CURRENT_TIMESTAMP
WHERE ID = #{id}

UPDATE TBL_MEMBER
SET MEMBER_STATUS = 'DISABLE', MEMBER_UPDATE_TIME = CURRENT_TIMESTAMP
WHERE ID = #{id}
